import React, { useState } from 'react';

const Status = () => {

    state = {
        data: []
    }

    const componentDidMount = () => {
        fetch('http://localhost:5000/api/Plan/1')
        .then((response) => response.json())
        .then(booksList => {
            this.setState({ books: booksList });
        });
    }


    // const getStatus = async () => {
    //     const res = await fetch('http://localhost:5000/api/Plan/1')
    //     const data = await res.json()
    //     return data
    //   }

    //   getStatus()



    var color = " "
    var cat = " "


    if(data.bmi <= 18.5 ){

        color = "rgb(134 177 217)"
        cat = "under weigth"

    }

    else if(data.bmi <= 24.9){

        color = "rgb(61 211 101)"
        cat = "Normal"

    }

    else if(data.bmi <= 29.9){

        color = "rgb(238 225 51)"
        cat = "Over weigth"

    }

    else if(data.bmi <= 34.9){

        color = "rgb(253 128 46)"
        cat = "Obese"

    }

    else{

        color = "rgb(249 83 83)"
        cat = "Extermely obese"

    }


    return (


            <div className='status'>

                <div className='row text-center stats'>
                    <div className='col-4 brf' style={{borderLeft:'0px'}}><p className='mt-2 statTitle'>Body mass index</p><br />
                    <div className='bar mb-2'>  
                    <div style={{color:color}} className='bmi'>
                                     {data.bmi}
            <p className='bmiCat'>-{cat}</p>

        </div></div>

                    </div>
                    <div className='col-4 brt'><p className='mt-2 statTitle'>Body fat percentage %</p><br />
                    <div className='cbar mb-2'>
                    <div className="fat">{data.fat}%</div>
                    </div></div>
                    <div className='col-4 brf' style={{borderRight:'0px'}}><p className='mt-2 statTitle'>Average calorie intake(needed)</p><br />
                    <div className='cbar mb-2'>
                    <p className='statTitle' style={{fontSize:'2rem'}} >{data.calorie}kc</p>
                    </div></div>
                </div>

                <div className='row text-center stats'>
                <div className='col-4 brr'><p className='mt-3 statTitle'>Exercise plan</p>
                <br />
                    <div className='cbar mb-2'>
                    <p className='statTitle' style={{fontSize:'2rem'}} >{data.plan}</p>
                    </div>
                </div>
                    <div className='col-4 brr'><p className='mt-3 statTitle'>Week</p>

                    <br />
                    <div className='cbar mb-2'>
                    <p className='statTitle' style={{fontSize:'3rem'}} >{data.week}</p>
                    </div>
                    </div>
                    <div className='col-4'><p className='mt-3 statTitle'>Diffculty</p>

                    <br />
                    <div className='cbar mb-2'>
                    <p className='statTitle' style={{fontSize:'2rem'}} >{data.level}</p>
                    </div>
                    </div>
                </div>


            </div>


    )
}

export default Status
