import { useState, useEffect } from 'react'
import { useHistory as History } from 'react-router-dom';

const Status = () => {

    const [status, setStatus] = useState({})

    useEffect(() => {

        const getStat = async () => {
            const stats = await getStatus()
            console.log(stats)
            setStatus(stats)
        }

        getStat()
    }, [])


    const getStatus = async () => {

        const response = await fetch(`http://localhost:5000/api/userinfo/${sessionStorage.getItem('tokenId')}`);
        const data = await response.json()
        return data
    }

    getStatus()


    const addUser = async () => {

        const settings = {

            method: 'POST',
            mode: 'cors',
            body: sessionStorage.getItem("tokenId"),
            headers: {
                'Accept': 'application/json',
                'Content-Type': 'application/json',
            }
        };

        const res = await fetch('http://localhost:5000/api/newuser/', settings)

        const data = await res.json()

        return data

    }


    var color = " "
    var cat = " "
    let history = History();

    if (status) {

        console.log(status['uInfo'].bmi)

        if (status['uInfo'].bmi <= 18.5 & status['uInfo'].bmi > 0) {

            color = "rgb(134 177 217)"
            cat = "under weigth"

        }

        else if (status['uInfo'].bmi <= 24.9) {

            color = "rgb(61 211 101)"
            cat = "Normal"

        }

        else if (status['uInfo'].bmi <= 29.9) {

            color = "rgb(238 225 51)"
            cat = "Over weigth"

        }

        else if (status['uInfo'].bmi <= 34.9) {

            color = "rgb(253 128 46)"
            cat = "Obese"

        }

        else {

            color = "rgb(249 83 83)"
            cat = "Extermely obese"

        }
    }

    else {

        addUser()
        history.push('/profile/')

    }


}

export default Status
