import { GoogleLogin } from "react-google-login"
import {useHistory} from 'react-router-dom';
import Logo from './Logo'

const loginCon = () => {

    let history = useHistory();

    var autenticate=(response)=>{   

        history.push('/profile/status')
        console.log(response);
        fetch(`/api/authorize/${response.tokenId}`)
        .then(res => res.json)
        .then(result => {
          console.log(result);
        })
        // joining private room in socket
        props.socket.emit("join",{"username":response.profileObj.givenName, "room":response.googleId})

        sessionStorage.setItem("tokenId", response.tokenId)
        sessionStorage.setItem("my_id", response.googleId)
        sessionStorage.setItem("chatting_with", response.googleId)
        sessionStorage.setItem("chatting_with_username", response.profileObj.givenName)
    };

      //Google btn
      const googleBtn = <GoogleLogin
        clientId="405204557273-prd5vmng5vcshg3ul604gl97reom0i7r.apps.googleusercontent.com"
        onSuccess={autenticate}
        onFailure={autenticate} />

    return (

        <div className='loginCon'>

            <div className="text-center">

                <Logo classname='homeLogo' />

            </div>

            <div className='text-center loginBtn'>

                {googleBtn}

            </div>

        </div>
    )
}

export default loginCon
