import { useState, useEffect } from 'react'

const Quotes = () => {

  function randomNumber(min, max) { 
    return parseInt(Math.random() * (max - min) + min)
}
  let rand = randomNumber(0,8)

    const [quote, setQuote] = useState({})

    useEffect(() => {
        const getQuotes = async () => {
          const quote = await getQuote()

          setQuote(quote)
        }

        getQuotes()
      }, [])

    async function getQuote(){

        let response = await fetch('http://127.0.0.1:5000/api/quote/');
        let quotes = await response.json()
        return quotes

      } 

      let displayedQuote = async () =>{return quote[rand].quote} 
      let quoteAuthor = async () =>{return quote[rand].author} 

      const x = displayedQuote()
      const y = quoteAuthor()

    return (
        <div className='text-center quoteCon'>

            <p className='quote'>

        {x}

            </p>

            <p className='author'>{y} </p>

        </div>
    )
}

export default Quotes
