import { useState, useEffect } from 'react'
import { BrowserRouter as Router, Route, Switch } from 'react-router-dom'
import UserBody from './UserBody'

const Status = () => {

    const [status, setStatus] = useState({})

    useEffect(() => {

        const getStat = async () => {
            const stats = await getStatus()

            setStatus(stats)
        }

        getStat()
    }, [])

    const getStatus = async () => {

        const res = await fetch(`http://localhost:5000/api/userinfo/${sessionStorage.getItem("tokenId")}`)
        const data = await res.json()
        console.log(data)
        return data
    }


    const addUser = async () => {

        const settings = {

            method: 'POST',
            mode: 'cors',
            body: sessionStorage.getItem("tokenId"),
            headers: {
                'Accept': 'application/json',
                'Content-Type': 'application/json',
            }
        };

        const res = await fetch('http://localhost:5000/api/newuser/', settings)

        const data = await res.json()

        return data

    }


    var color = " "
    var cat = " "

    if (status != 404) {

        console.log(status)

        if (status.bmi <= 18.5 & status.bmi > 0) {

            color = "rgb(134 177 217)"
            cat = "under weigth"

        }

        else if (status.bmi <= 24.9) {

            color = "rgb(61 211 101)"
            cat = "Normal"

        }

        else if (status.bmi <= 29.9) {

            color = "rgb(238 225 51)"
            cat = "Over weigth"

        }

        else if (status.bmi <= 34.9) {

            color = "rgb(253 128 46)"
            cat = "Obese"

        }

        else {

            color = "rgb(249 83 83)"
            cat = "Extermely obese"

        }
    }

    else {

        addUser()
        window.location.href = "http://localhost:3000/profile"


    }


    return (

<Router>
        <div className='status'>
     <Route
          exact
          path="/profile/"
          component={() => <UserBody />}
        />
            <div className='row text-center stats'>
                <div className='col-4 brf' style={{ borderLeft: '0px' }}><p className='mt-2 statTitle'>Body mass index</p><br />
                    <div className='bar mb-2'>
                        <div style={{ color: color }} className='bmi'>
                            {0}
                            <p className='bmiCat'>-{cat}</p>

                        </div></div>

                </div>
                <div className='col-4 brt'><p className='mt-2 statTitle'>Body fat percentage %</p><br />
                    <div className='cbar mb-2'>
                        <div className="fat">{status.fat}%</div>
                    </div></div>
                <div className='col-4 brf' style={{ borderRight: '0px' }}><p className='mt-2 statTitle'>Average calorie intake(needed)</p><br />
                    <div className='cbar mb-2'>
                        <p className='statTitle' style={{ fontSize: '2rem' }} >{status.calorie}kc</p>
                    </div></div>
            </div>

            <div className='row text-center stats'>
                <div className='col-4 brr'><p className='mt-3 statTitle'>Exercise plan</p>
                    <br />
                    <div className='cbar mb-2'>
                        <p className='statTitle' style={{ fontSize: '2rem' }} >{status.plan}</p>
                    </div>
                </div>
                <div className='col-4 brr'><p className='mt-3 statTitle'>Week</p>

                    <br />
                    <div className='cbar mb-2'>
                        <p className='statTitle' style={{ fontSize: '3rem' }} >{status.week}</p>
                    </div>
                </div>
                <div className='col-4'><p className='mt-3 statTitle'>Diffculty</p>

                    <br />
                    <div className='cbar mb-2'>
                        <p className='statTitle' style={{ fontSize: '2rem' }} >{status.level}</p>
                    </div>
                </div>
            </div>


        </div>
        </Router>

    )
}

export default Status
