import { useState, useEffect } from 'react'

const Diet = () => {

    const [plan, setplan] = useState({})

    useEffect(() => {

        const getplan = async () => {
            const Uplan = await getUserplan()

            setplan(Uplan)
        }

        getplan()

    }, [])

    const [diet, setDiet] = useState({})

    useEffect(() => {

        const getdite = async () => {
            const diet = await getDite()

            setDiet(diet)
        }

        getdite()

    }, [])


    const getUserplan = async () => {
        const response = await fetch(`http://localhost:5000/api/userinfo/${sessionStorage.getItem("tokenId")}`);
        const plan = await response.json()
        return plan

    }

    const getDite = async () => {
        const response = await fetch('http://127.0.0.1:5000/api/diet/');
        const dite = await response.json()
        return dite
    }


    let DITE  = ' '
    let w

    if(diet[0]!==undefined){

        for(w=0;w<diet.lenght;w++){

            DITE= diet[w]['plan']
            console.log(DITE)
        }

    }


    if (plan['plan']) {

        return (

            <div className='mr-5 ml-5 plans'>
                <p className='text-center mt-5'><b>{ }</b></p>

                <div className='day row mt-5'>

                    <div className='col-3 text-center'>Breakfast<br /></div>

                    <div className='col-3 text-center'>Lunch<br /></div>

                    <div className='col-3 text-center'>Supper<br /></div>

                    <div className='col-3 text-center'>Dinner<br />

                    </div>
                </div>

                <hr />
            </div>

        )
    }

    else {

        return (

            <div className='noPlan'>
                No plan selected!
            </div>
        )

    }

}

export default Diet
